method.custom:
time_t *array;
GArray *garray;
gint result;
g_return_val_if_fail (rule != NULL, 0);
array = g_new (time_t, count);
result = icalrecur_expand_recurrence (rule, start, count, array);
if (result == 0) {
        g_free (array);
        return NULL;
} else {
        garray = g_array_new (FALSE, FALSE, sizeof (time_t));
        g_array_append_vals (garray, array, count);
        g_free (array);
        return garray;
}
