method.custom:
vcardstrarray *field;
g_return_val_if_fail(self != NULL, NULL);
field = vcardstructured_field_at((vcardstructuredtype *)i_cal_object_get_native((ICalObject *)self), position);
if (field == NULL) {
    return NULL;
}
return i_cal_vcard_str_array_new_full(field, NULL);

g_return_if_fail(self != NULL);
g_return_if_fail(field != NULL);
vcardstructured_set_field_at(
    (vcardstructuredtype *)i_cal_object_get_native((ICalObject *)self),
    position,
    vcardstrarray_clone((vcardstrarray *)i_cal_object_get_native((ICalObject *)field)));
