#define minInt(a, b) ((a) < (b) ? (a) : (b))

int icalss_input(char *buf, size_t max_size)
{
    size_t n = minInt(max_size, strlen(input_buffer_p));

    if (n > 0) {
        memcpy(buf, input_buffer_p, n);
        input_buffer_p += n;
        return (int)n;
    } else {
        return 0;
    }
}

